package com.example.QuizApp.controller;import com.example.QuizApp.model.Quiz;import com.example.QuizApp.model.Response;import com.example.QuizApp.service.QuizService;import com.example.QuizApp.wrapper.QuestionWrapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("quiz")public class QuizController {    @Autowired    QuizService quizService;    @PostMapping("add")    public ResponseEntity<String> createQuiz(@RequestParam String category, @RequestParam int noQun, @RequestParam String title) {        return quizService.createQuiz(category, noQun, title);    }    @GetMapping("get/{id}")    public ResponseEntity<List<QuestionWrapper>> getQuizQuestions(@PathVariable int id) {        return quizService.getQuizQuestions(id);    }    @PostMapping("submit/{id}")    public ResponseEntity<Integer> submitQuiz(@PathVariable Integer id, @RequestBody List<Response> responses) {        return quizService.submitQuiz(id, responses);    }    @DeleteMapping("delete/{id}")    public ResponseEntity<String> deleteQuiz(@PathVariable int id) {        return quizService.deleteQuiz(id);    }    @GetMapping("getAllQuiz")    public ResponseEntity<List<Quiz>> getAllQuiz() {        return quizService.getAllQuiz();    }}