package com.example.QuizApp.service;import com.example.QuizApp.dao.QuestionDAO;import com.example.QuizApp.model.Question;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Service;import static com.example.QuizApp.utils.CommonUtil.generateRandomNumber;import java.util.List;@Servicepublic class QuestionService {    @Autowired    QuestionDAO questionDAO;    public ResponseEntity<List<Question>> getAllQuestions() {        return new ResponseEntity<>(questionDAO.findAll(), HttpStatus.OK);    }    public ResponseEntity<List<Question>> getQuestionsByCategory(String category) {        return new ResponseEntity<>(questionDAO.findByCategory(category), HttpStatus.OK);    }    public ResponseEntity<String> addQuestion(Question question) {        question.setId(generateRandomNumber(4));        questionDAO.save(question);        return new ResponseEntity<>("Successfully saved", HttpStatus.CREATED);    }    public ResponseEntity<String> deleteQuestion(Question question) {        questionDAO.delete(question);        return new ResponseEntity<>("Successfully deleted.", HttpStatus.OK);    }    public ResponseEntity<String> updateQuestion(Question question) {        questionDAO.save(question);        return new ResponseEntity<>("Successfully updated.", HttpStatus.OK);    }}